from django.urls import path, includefrom rest_framework.routers import DefaultRouterfrom .views import SocialViewset, PortfolioViewset, ProjectViewset, InfoViewset, XabarlarViewsetrouter = DefaultRouter()router.register('social', SocialViewset)router.register('portfolio', PortfolioViewset)router.register('Project', ProjectViewset)router.register('info', InfoViewset)router.register('xabar', XabarlarViewset)urlpatterns = [    path('', include(router.urls))]